import React, { useState } from "react";


function Form() {
  const [formInfo, setFormInfo] = useState({
    freeCodeCamp: "",
    blogPost: "",
    typing: "",
    codeWars: "",
    isCompleted: false,
  });


  const { freeCodeCamp, blogPost, codeWars,typing } = formInfo;
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormInfo({ ...formInfo, [name]: value });
  };
  const handleSubmit = (e) => {
    const { freeCodeCamp, codeWars,typing,blogPost } = formInfo;
    e.preventDefault();

    if ((freeCodeCamp, codeWars, typing, blogPost)) {


      setFormInfo({...formInfo,
        freeCodeCamp: "",
        blogPost: "",
        codeWars: "",
        typing: "",
      });
    }
  };
  return (
    <div>
    <h1>TCG-TRACKER-APP</h1>
    <div className="learners-form">
      <form onSubmit={handleSubmit}>

        <input
          type="text"
          name="freeCodeCamp"
          id="freeCodeCamp"
          placeholder="freeCodeCamp"
          onChange={handleChange}
          value={freeCodeCamp}
          required
        />

        <input
          type="number"
          name="codeWars"
          id="codeWars"
          placeholder="codeWars"
          onChange={handleChange}
          value={codeWars}
          required
        />

        <input
          type="number"
          name="typing"
          id="typing"
          placeholder="typing"
          onChange={handleChange}
          value={typing}
          required
        />
        <input
          type="url"
          name="blogPost"
          id="blogPost"
          placeholder="blogPost"
          onChange={handleChange}
          value={blogPost}
          required
        />
        <button type="submit">Submit</button>
      </form>
    </div>
    </div>
  );
}

export default Form;
