import React, { useState, useSelector } from "react";
import { Table } from "reactstrap";


function UpdateForm() {
  const progress = useSelector((state) => state.form.form);
const [isEditable, setIsEditable] = useState(false);
  const openEditForm = (id) => {
    return progress.find((learner) => {
      if (learner.id === id) {
        setIsEditable(!isEditable);
      }
      return learner;
    });
  };
  return (
    <div className="display-table">
      <Table dark>
        <thead>
          <tr>
            <th>FreeCodeCamp</th>
            <th>CodeWars</th>
            <th>Typing</th>
            <th></th>
            <th></th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {progress.length > 0 &&
            progress.map((learner) => {
              const { id, name, surname, progress, isCompleted } = learner;

              return (
                <tr key={id} className={`${isCompleted ? "complete" : ""} `}>
                  <td>{name}</td>
                  <td>{surname}</td>
                  <td>
                    {isEditable ? progress : <EditProgress {...employee} />}
                  </td>
                  {
                    <div className="display-button">
                      <div>
                        <button onClick={() => completeAndGetEmployees(id)}>
                          Complete
                        </button>
                      </div>
                      <div>
                        <button onClick={() => openEditForm(id)}>Edit</button>

                        <button onClick={() => (deleteEmployee(id))}>
                          Delete
                        </button>
                      </div>
                    </div>
                  }
                </tr>
              );
            })}
        </tbody>
      </Table>
    </div>
  );
}

export default UpdateForm;
