import React, { useState } from "react";


function Form() {
  const [formInfo, setFormInfo] = useState({
    freeCodeCamp: "",
    blogPost: "",
    typing: "",
    codeWars: "",
    isCompleted: false,
  });


  const { freeCodeCamp, blogPost, codeWars,typing } = formInfo;
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormInfo({ ...formInfo, [name]: value });
  };
  const handleSubmit = (e) => {
    const { freeCodeCamp, codeWars,typing,blogPost } = formInfo;
    e.preventDefault();

    if ((freeCodeCamp, codeWars, typing, blogPost)) {


      setFormInfo({...formInfo,
        freeCodeCamp: "",
        blogPost: "",
        codeWars: "",
        typing: "",
      });
    }
  };
  return (
    <div className="employee-form">
      <form onSubmit={handleSubmit}>

        <input
          type="text"
          name="name"
          id="name"
          onChange={handleChange}
          value={freeCodeCamp}
          required
        />

        <input
          type="number"
          name="surname"
          id="surname"
          placeholder="Enter surname"
          onChange={handleChange}
          value={codeWars}
          required
        />

        <input
          type="number"
          name="progress"
          id="progress"
          onChange={handleChange}
          value={typing}
          required
        />
        <input
          type="number"
          name="progress"
          id="progress"
          onChange={handleChange}
          value={blogPost}
          required
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default Form;
