import React, { useState, useSelector } from "react";
import { Table } from "reactstrap";
import Edit from "./Edit";


function UpdateForm() {
  const progress = useSelector((state) => state.form.form);
const [isEditable, setIsEditable] = useState(false);
  const openEditForm = (id) => {
    return progress.find((learner) => {
      if (learner.id === id) {
        setIsEditable(!isEditable);
      }
      return learner;
    });
  };
  return (
    <div className="display-table">
      <table dark>
        <thead>
          <tr>
            <th>FreeCodeCamp</th>
            <th>CodeWars</th>
            <th>Typing</th>
            <th>blogPost</th>
            <th></th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {progress.length > 0 &&
            progress.map((learner) => {
              const { id, freeCodeCamp, typing, blogPost, codeWars, isCompleted } = learner;

              return (
                <tr key={id} className={`${isCompleted ? "complete" : ""} `}>
                  <td>{freeCodeCamp}</td>
                  <td>{typing}</td>
                  <td>{blogPost}</td>
                  <td>{codeWars}</td>
                  <td>
                    {isEditable ? progress : <Edit {...learner} />}
                  </td>
                  {
                    <div>
                      <div>
                        <button onClick={() => (id)}>
                          Complete
                        </button>
                      </div>
                      <div>
                        <button onClick={() => openEditForm(id)}>Edit</button>

                        <button onClick={() => ((id))}>
                          Delete
                        </button>
                      </div>
                    </div>
                  }
                </tr>
              );
            })}
        </tbody>
      </table>
    </div>
  );
}

export default UpdateForm;
