import React, {useState} from 'react';


function Form() {

  const [formData, setFormData] = useState({
    freeCodeCamp: "",
    typing: "",
    codeWars: "",
    blogPost: "",
  });
  const handleSubmit = (event) => {
    event.preventDefault();
    const { freeCodeCamp, typing, codeWars, blogPost } = formData;
    if ((freeCodeCamp, typing, codeWars, blogPost)) {
      setFormData({
        freeCodeCamp: "",
        typing: "",
        codeWars: "",
        blogPost: "",
      });
    }
  };
  const { firstName, lastName, workingOn } = formData;
  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  return (
    <div className="learners">
      <form onSubmit={handleSubmit}>
        <input
          type="text" name="freeCodeCamp" placeholder="FreeCodeCamp:"  onChange={handleChange} value={freeCodeCamp}
        />
        <input
          type="text" name="typing" placeholder="Typing:"  onChange={handleChange} value={typing}
        />
        <input
          type="text" name="blogPost" placeholder="BlogPost:" onChange={handleChange} value={blogPost}
        />
        <input
          type="text" name="codeWars" placeholder="CodeWars:" onChange={handleChange} value={codeWars}
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default Form;
